export const appStyles = {
  app: {
    fontFamily: 'Arial, sans-serif',
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px',
  },
  header: {
    backgroundColor: '#00704A',
    color: 'white',
    padding: '20px',
    textAlign: 'center',
  },
  nav: {
    listStyleType: 'none',
    padding: 0,
  },
  navItem: {
    display: 'inline',
    marginRight: '20px',
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
  },
  main: {
    padding: '20px',
  },
  footer: {
    textAlign: 'center',
    marginTop: '40px',
    padding: '20px',
    backgroundColor: '#f0f0f0',
  },
};
